# Import the main modules of expyrimentfrom expyriment import design, control, stimuli# Create an object of class Experimentexp = design.Experiment(name="Square")control.initialize(exp)# Create a fixation crossfixation = stimuli.FixCross()# Create a 50px square (blue)square = stimuli.Rectangle(size=(50, 50), colour=(0, 0, 255))# Start running the experimentcontrol.start(subject_id=1)# Present square first, then fixation cross on top of itsquare.present(clear=True, update=True)fixation.present(clear=False, update=True)# Leave them on-screen for 500 msexp.clock.wait(500)# Now show only the square (clear screen, then draw square)square.present(clear=True, update=True)# Leave it on-screen until a key is pressedexp.keyboard.wait()# End the current session and quit expyrimentcontrol.end()