# Import the main modules of expyrimentfrom expyriment import design, control, stimuli# Create experimentexp = design.Experiment(name="Two Squares")control.initialize(exp)# Squares: 50x50, centered as a pair with 200 pixels between the centerleft_square  = stimuli.Rectangle(size=(50, 50), colour=(255, 0, 0), position=(-100, 0))  # red, leftright_square = stimuli.Rectangle(size=(50, 50), colour=(0, 255, 0), position=(100, 0))   # green, right# Start experimentcontrol.start(subject_id=1) # #,skip_ready=True))# Draw both squares: clear once, then overlay second without clearingleft_square.present(clear=True, update=False)right_square.present(clear=False, update=True)# Wait for key pressexp.keyboard.wait()# End experimentcontrol.end()